mapper.select:
SELECT count(DISTINCT f.id) FROM t_approve_flow f LEFT JOIN t_approve_detail d ON f.id = d.flow_id AND f.point_id = d.point_id WHERE 1 = 1
AND d.approver = #{userGuid,jdbcType=VARCHAR}
AND f.status = 0 AND d.result IS NULL
AND f.status != 0
AND apply_time BETWEEN #{startDate} AND #{endDate}
AND INSTR(applicant_name, #{applicantOrType})

SELECT count(DISTINCT f.id) FROM t_approve_flow f LEFT JOIN t_approve_detail d ON f.id = d.flow_id AND f.point_id = d.point_id WHERE 1 = 1
AND d.approver = #{userGuid,jdbcType=VARCHAR}
AND f.status = 0 AND d.result IS NULL
AND f.status != 0
AND apply_time BETWEEN #{startDate} AND #{endDate}
AND f.applicant_id = #{uuid,jdbcType=VARCHAR}

SELECT count(1) FROM t_approve_flow f LEFT JOIN t_approve_detail d ON f.id = d.flow_id AND f.point_id = d.point_id WHERE 1 = 1
AND d.approver = #{userGuid,jdbcType=VARCHAR}
AND f.status = #{status,jdbcType=INTEGER}
AND f.status != 0
